// Safely extract HTML from the actual structure
const html =
  $json.content?.parts?.[0]?.text ?? '';

// Convert HTML ‚Üí plain text
const text = html
  .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
  .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
  .replace(/<\/p>/gi, '\n\n')
  .replace(/<br\s*\/?>/gi, '\n')
  .replace(/<[^>]+>/g, '')
  .replace(/\n{3,}/g, '\n\n')
  .trim();

return {
  subject: $json.subject ?? 'Tech & AI Briefing',
  linkedinText: `${$json.subject ?? 'Tech & AI Briefing'}\n\n${text}`
};

