// Take only the first 10 items from the sorted array
const items = $input.all();
const top10 = items.slice(0, 10);

return top10;

const items = $input.all();

let articlesList = "";

items.forEach((item, index) => {
  const title = item.json.title || 'No title';
  const link = item.json.link || item.json.url || 'No link';
  const pubDate = item.json.pubDate || item.json.isoDate || 'Unknown date';
  const description = item.json.contentSnippet || item.json.description || 'No description';
  const source = item.json.creator || item.json.author || 'Unknown source';

  articlesList += `${index + 1}. **${title}**\n`;
  articlesList += `   Source: ${source}\n`;
  articlesList += `   Published: ${pubDate}\n`;
  articlesList += `   Link: ${link}\n`;
  articlesList += `   Summary: ${description}\n\n`;
});

return [{
  json: {
    articlesList: articlesList,
    Keyword: "AI trends and tools", // Default keyword - can be customized
    Description: "AITechScope is a leading provider of virtual assistant services, specializing in AI-powered automation, n8n workflow development, and business process optimization. We help businesses leverage cutting-edge AI tools and technologies to scale operations, reduce costs, and improve efficiency through intelligent delegation and automation solutions."
  }
}];

