// --- Grab model output (works for OpenAI + Gemini) ---
const j = items[0].json;

let text =
  j?.message?.content ??                              // OpenAI (some n8n nodes)
  j?.choices?.[0]?.message?.content ??                // OpenAI (raw API shape)
  j?.content?.parts?.[0]?.text ??                     // Gemini common shape
  j?.candidates?.[0]?.content?.parts?.[0]?.text ??    // Gemini alternative
  null;

if (!text) {
  throw new Error('Model output not found on known paths.');
}

text = String(text).trim();

// --- Strip code fences if present ---
if (text.startsWith('```')) {
  // remove leading ```json (or ```), and trailing ```
  text = text.replace(/^```[a-z]*\n?/i, '').replace(/```$/, '').trim();
}

// --- Extract the first JSON object from the string safely ---
const start = text.indexOf('{');
const end   = text.lastIndexOf('}');
if (start === -1 || end === -1 || end <= start) {
  throw new Error('No JSON object found in model output:\n' + text.slice(0, 400));
}
const jsonStr = text.slice(start, end + 1);

// --- Parse JSON ---
let parsed;
try {
  parsed = JSON.parse(jsonStr);
} catch (e) {
  // try a softer parse if there are stray newlines/tabs
  parsed = JSON.parse(jsonStr.replace(/\n/g, ' ').replace(/\t/g, ' '));
}

// --- Guards ---
if (!parsed.title || !parsed.content) {
  throw new Error('Parsed JSON missing title/content keys. Got keys: ' + Object.keys(parsed).join(', '));
}

// --- Output for next nodes ---
return [
  {
    json: {
      title: parsed.title,
      content: parsed.content
    }
  }
];

