const MAX_LENGTH = 700; // Leave space for your fixed prompt text

const items = $input.all();

return items.map(item => {
  let text = item.json.output || "";

  if (text.length > MAX_LENGTH) {
    text = text.substring(0, MAX_LENGTH);
  }

  item.json.output = text;
  return item;
});

const items = $input.all();
let output = [];

for (const item of items) {
  for (const article of item.json.articles) {
    output.push({
      json: {
        title: article.title,
        url:article.url,
        image: article.image,
        description : article.description
      }
    });
  }
}

return output;

