const items = $input.all();
const capitalizedOutput = items.map((item) => {
  item.json.output = item.json.output.toUpperCase();
  return item;
});
return capitalizedOutput;

const items = $input.all();
let output = [];

for (const item of items) {
  for (const article of item.json.articles) {
    output.push({
      json: {
        title: article.title,
        url:article.url,
        image: article.image,
        description : article.description
      }
    });
  }
}

return output;

