// Get the first input item
const item = items[0];

// Extract AI output safely
const output = item.json.output;

// If output is an object with .subject/content — use them
// If it's a string — treat it as full content
let subject = "";
let content = "";

if (typeof output === "object" && (output.subject || output.content)) {
  subject = output.subject || "AI Summary";
  content = output.content || "";
} else {
  // Fallback for string-type output
  subject = "AI Summary";
  content = String(output || "");
}

// Clean and truncate text
const plainText = content
  .replace(/<[^>]*>/g, ' ')
  .replace(/\s+/g, ' ')
  .trim();

// Limit for LinkedIn
const maxContent = 2800 - subject.length;
const truncated =
  plainText.length > maxContent
    ? plainText.substring(0, maxContent) + '...'
    : plainText;

// Return result
return [
  {
    json: {
      subject,
      content: truncated,
    },
  },
];

