  "required": ["title", "topics"],
  "properties": {
    "title": {
      "type": "string",
      "description": "A short, descriptive title"
    },
    "topics": {
      "type": "array",
      "description": "An array of exactly three topics",
      "minItems": 3,
      "maxItems": 3,
      "items": {
        "type": "string"
      }
    }
  },
  "additionalProperties": false
}

parameters.jsCode:
// Clean HTML for LinkedIn
let content = $json.output.content;

// Remove HTML tags
content = content.replace(/<[^>]*>/g, '');

// Fix bold formatting (**text** to just text)
content = content.replace(/\*\*(.*?)\*\*/g, '$1');

// Convert HTML entities
content = content.replace(/&amp;/g, '&');
content = content.replace(/&lt;/g, '<');
content = content.replace(/&gt;/g, '>');

// Add emojis and better formatting for LinkedIn
content = content.replace(/Harnessing Niche AI for Everyday Problem Solving/g, 'ðŸ” HARNESSING NICHE AI FOR EVERYDAY PROBLEM SOLVING');
content = content.replace(/AI Tools to Supercharge Your Workflow/g, 'ðŸš€ AI TOOLS TO SUPERCHARGE YOUR WORKFLOW');

// Format bullet points
content = content.replace(/Merlin Bird ID/g, 'â€¢ Merlin Bird ID');
content = content.replace(/Goblin\.Tools/g, 'â€¢ Goblin.Tools');
content = content.replace(/OpusClip/g, 'â€¢ OpusClip');
content = content.replace(/Rewind\.ai/g, 'â€¢ Rewind.ai');
content = content.replace(/Be My AI/g, 'â€¢ Be My AI');
content = content.replace(/Character\.AI/g, 'â€¢ Character.AI');
content = content.replace(/Gamma/g, 'â€¢ Gamma');
content = content.replace(/SciSpace Agent/g, 'â€¢ SciSpace Agent');

// Add hashtags at the end
content += '\n\n#AI #Productivity #Workflow #TechTools #Innovation';

// Ensure under 3000 characters
if (content.length > 2900) {
  content = content.substring(0, 2900) + '...';
}

return { 
  subject: $json.subject,
  cleanContent: content 
};

