// List of countries from around the world
const countries = [
  'Japan', 'Brazil', 'Iceland', 'Morocco', 'New Zealand', 'Peru', 'Thailand',
  'Norway', 'Egypt', 'Australia', 'Kenya', 'Greece', 'India', 'Mexico',
  'South Korea', 'Italy', 'Indonesia', 'Turkey', 'South Africa', 'Argentina',
  'Canada', 'Vietnam', 'Portugal', 'Chile', 'UAE', 'Ireland', 'Poland',
  'Switzerland', 'Malaysia', 'Colombia', 'Netherlands', 'Singapore', 'Spain',
  'Sweden', 'Philippines', 'Austria', 'Belgium', 'Denmark', 'Finland', 'Croatia', 'Oman' ,'Dubai' , 'South Afirica' ,
];

// Unique topic categories for each country
const topicCategories = [
  'traditional cuisine and unique dishes',
  'ancient historical landmarks',
  'unique wildlife and biodiversity',
  'traditional festivals and celebrations',
  'architectural wonders',
  'indigenous cultures and traditions',
  'natural wonders and landscapes',
  'innovative technology and inventions',
  'traditional music and dance',
  'unique customs and etiquette',
  'legendary myths and folklore',
  'traditional crafts and art forms',
  'rare geographical features',
  'historical figures and heroes',
  'unique sports and games'
];

// Randomly select a country
const randomCountry = countries[Math.floor(Math.random() * countries.length)];

// Randomly select a topic category
const randomTopic = topicCategories[Math.floor(Math.random() * topicCategories.length)];

// Return the selected country and topic
return [{
  json: {
    country: randomCountry,
    topic: randomTopic,
    timestamp: new Date().toISOString()
  }
}];

